CREATE EXTENSION IF NOT EXISTS "uuid-ossp"; CREATE TABLE measurements (id UUID PRIMARY KEY DEFAULT uuid_generate_v4(), filename TEXT NOT NULL, total_area FLOAT NOT NULL, predominant_pitch TEXT NOT NULL, ridges FLOAT, hips FLOAT, valleys FLOAT, rakes FLOAT, eaves FLOAT, flashing FLOAT, step_flashing FLOAT, waste_percentage INTEGER, penetrations_area FLOAT, penetrations_perimeter FLOAT, areas_per_pitch JSONB, created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(), updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW());
